import React, { useState } from 'react';
import './PropertyCard.css';

const PropertyCard = ({ property, onTitleClick, onCloseForm }) => {
  const [isImageVisible, setIsImageVisible] = useState(true); // State to track image visibility

  const handleTitleClick = () => {
    onTitleClick(property);
  };

  const handleCloseClick = () => {
    onCloseForm();
  };

  return (
    <div className="property-card">
      {/* Toggle the hide class based on isImageVisible state */}
      <img
        className={`property-image ${!isImageVisible ? 'hide' : ''}`}
        src={property.image}
        alt={property.title}
        onClick={handleTitleClick}
      />

      {/* Displaying property details */}
      <div className="property-details">
        <h3 onClick={handleTitleClick}>{property.title}</h3>
        <p>{property.description}</p>
        <p>Цена: {property.price}</p>
      </div>
    </div>
  );
};

export default PropertyCard;
