import React, { useState, useEffect } from 'react';

import PropertyCard from './PropertyCard';
import PropertyDetailsForm from './PropertyDetailsForm';
import './HomePage.css';

const HomePage = () => {
  const [imageIndexes, setImageIndexes] = useState({
    beautifulHouse: 0,
    cozyApartment: 0,
  });
  
  const [selectedProperty, setSelectedProperty] = useState(null);
  const [isOpen, setIsOpen] = useState(false);

  const property = {
    title: 'Южный квартал',
    image1: '/image1.jpg', // Путь к первому изображению
    image2: '/image2.jpg', // Путь к второму изображению
    image3: '/image3.jpg', // Путь к третьему изображению
    image4: '/image4.jpg', // Путь к четвертому изображению
    description: 'Описание недвижимости',
    price: '6 000 000 рублей',
    location: 'Владивосток',
    location2: '58 кв. м.'
    // Другие свойства недвижимости
  };

  const beautifulHouseImages = ['/house-1.jpg', '/house-2.jpg', '/house-3.jpg'];
  const cozyApartmentImages = ['/apartment-1.jpg', '/apartment-2.jpg', '/apartment-3.jpg'];


  useEffect(() => {
    const interval = setInterval(() => {
      setImageIndexes(prevIndexes => ({
        ...prevIndexes,
        beautifulHouse: (prevIndexes.beautifulHouse + 1) % beautifulHouseImages.length,
        cozyApartment: (prevIndexes.cozyApartment + 1) % cozyApartmentImages.length,
      }));
    }, 5000);
  
    return () => clearInterval(interval);
  }, [beautifulHouseImages, cozyApartmentImages]);
  
  

  const handleTitleClick = (property) => {
    setSelectedProperty(property);
    setIsOpen(true);
    // Redirect to the search page when clicking on the property title
    history.push('/search');
  };

  const handleCloseForm = () => {
    setIsOpen(false);
    setSelectedProperty(null); // Reset the selected property when closing the form
  };

  return (
    <div className="homepage-container">
      <h1 className="homepage-heading">Добро пожаловать на главную страницу нашего приложения по недвижимости</h1>
      <div className="property-list">
        <h2>Популярные предложения</h2>
        <div className="property-cards">
          <div>
            <PropertyCard
              key="beautifulHouse"
              property={{
                id: 1,
                title: 'Прекрасный дом',
                description: 'Просторный дом с видом на океан',
                price: '15 000 000 рублей',
                location: 'Владивосток',
                image: beautifulHouseImages[imageIndexes.beautifulHouse],
              }}
              onTitleClick={handleTitleClick}
              onCloseForm={handleCloseForm} // Pass the handleCloseForm function
            />
          </div>
          <div>
            <PropertyCard
              key="cozyApartment"
              property={{
                id: 2,
                title: 'Уютная квартира',
                description: 'Современная квартира в центре города',
                price: '8 000 000 рублей',
                location: 'Артем',
                image: cozyApartmentImages[imageIndexes.cozyApartment],
              }}
              onTitleClick={handleTitleClick}
              onCloseForm={handleCloseForm} // Pass the handleCloseForm function
            />
          </div>
        </div>
      </div>
      {selectedProperty && isOpen && <PropertyDetailsForm property={property} onCloseForm={handleCloseForm} />}
    </div>
  );
};

export default HomePage;
