import React from 'react';
import './PropertyCard.css';

const PropertyCard = ({ property, onTitleClick, onCloseForm }) => {
  const handleTitleClick = () => {
    onTitleClick(property);
  };

  const handleCloseClick = () => {
    onCloseForm();
  };

  return (
    <div className="property-card">
      <img className="property-image" src={property.image} alt={property.title} /> {<PropertyCard
  property={{
    id: 1,
    title: 'Барн хаус',
    description: 'Каркасный дом',
    price: '9 000 000 рублей',
    image: '/images/house.jpg', // Пример пути к изображению
  }}
  onTitleClick={handleTitleClick}
  onCloseForm={handleCloseForm}
/>
/* Вставляем изображение */}
      <h3 onClick={handleTitleClick}>{property.title}</h3>
      <p>{property.description}</p>
      <p>Цена: {property.price}</p>
    </div>
  );
};

export default PropertyCard;
