import React, { useState, useEffect } from 'react';
import PropertyCard from './PropertyCard';
import PropertyDetailsForm from './PropertyDetailsForm';
import './HomePage.css';
import SearchProperty from './SearchProperty';

const HomePage = () => {
  const [selectedProperty, setSelectedProperty] = useState(null);
  const [isOpen, setIsOpen] = useState(false);
  const [filteredProperties, setFilteredProperties] = useState([]); // State to store filtered properties

  const properties = [
    {
      id: 1,
      title: 'Южный квартал',
      description: 'Просторный дом с видом на океан',
      price: '15000000 рублей',
      location: 'Владивосток',
      image: '/house-1.jpg',
    },
    {
      id: 2,
      title: 'Дом у реки',
      description: 'Уютный дом с садом возле реки',
      price: '6 000 000 рублей',
      location: 'Артем',
      image: '/house-2.jpg',
    },
  ];

  // Функция для фильтрации свойств на основе критериев поиска
  const filterProperties = (criteria) => {
    const filtered = properties.filter(property => {
      const matchesSearchCriteria =
        (!criteria.city || property.location === criteria.city) &&
        (!criteria.priceFrom || parseInt(property.price) >= parseInt(criteria.priceFrom)) &&
        (!criteria.priceTo || parseInt(property.price) <= parseInt(criteria.priceTo)) &&
        (!criteria.propertyType || property.title.toLowerCase().includes(criteria.propertyType.toLowerCase()));
      return matchesSearchCriteria;
    });
    setFilteredProperties(filtered);
  };

  const handleTitleClick = (property) => {
    setSelectedProperty(property);
    setIsOpen(true);
  };

  const handleCloseForm = () => {
    setIsOpen(false);
    setSelectedProperty(null);
  };

  return (
    <div className="homepage-container">
      <h1 className="homepage-heading">Добро пожаловать на главную страницу нашего приложения по недвижимости</h1>
      <SearchProperty onSearch={filterProperties} />
      <div className="property-list">
        <h2>Результаты поиска</h2>
        <div className="property-cards">
          {filteredProperties.length > 0 ? (
            filteredProperties.map(property => (
              <div key={property.id}>
                <PropertyCard
                  property={property}
                  onTitleClick={handleTitleClick}
                  onCloseForm={handleCloseForm}
                />
              </div>
            ))
          ) : (
            <div>По вашему запросу ничего не найдено</div>
          )}
        </div>
      </div>
      {selectedProperty && isOpen && <PropertyDetailsForm property={selectedProperty} onCloseForm={handleCloseForm} />}
    </div>
  );
};

export default HomePage;
