import React, { useState, useEffect } from 'react';
import PropertyCard from './PropertyCard';
import PropertyDetailsForm from './PropertyDetailsForm';
import './HomePage.css';

const HomePage = () => {
  const [selectedProperty, setSelectedProperty] = useState(null);
  const [isOpen, setIsOpen] = useState(false);
  const [imageIndex, setImageIndex] = useState(0); // State to track current image index

  const property1Images = ['/1.jpg', '/2.jpg', '/3.jpg', '/4.jpg'];
  const property2Images = ['/apartment-1.jpg', '/apartment-2.jpg', '/apartment-3.jpg', '/apartment-4.jpg'];

  useEffect(() => {
    const interval = setInterval(() => {
      setImageIndex(prevIndex => (prevIndex + 1) % property1Images.length);
    }, 3000);
  
    return () => clearInterval(interval);
  }, []);

  const handleTitleClick = (property) => {
    setSelectedProperty(property);
    setIsOpen(true);
  };

  const handleCloseForm = () => {
    setIsOpen(false);
    setSelectedProperty(null);
  };

  const handlePrevImage = () => {
    setImageIndex(prevIndex => (prevIndex === 0 ? property1Images.length - 1 : prevIndex - 1));
  };

  const handleNextImage = () => {
    setImageIndex(prevIndex => (prevIndex + 1) % property1Images.length);
  };

  return (
    <div className="homepage-container">
      <h1 className="homepage-heading">Добро пожаловать на главную страницу нашего приложения по недвижимости</h1>
      <div className="property-list">
        <h2>Популярные предложения</h2>
        <div className="property-cards">
          <div className="property-gallery">
            <div className="gallery-image">
              <img src={property1Images[imageIndex]} alt="Property" />
              <button className="prev-btn" onClick={handlePrevImage}>{'<'}</button>
              <button className="next-btn" onClick={handleNextImage}>{'>'}</button>
            </div>
            <div className="property-info">
              <h3>{property.title}</h3>
              <p>{property.description}</p>
              <p>{property.location}</p>
              <p>{property.squaremeter}</p>
              <p>{property.price}</p>
              <button className="buy-btn">Купить проект</button>
            </div>
          </div>
        </div>
      </div>
      {selectedProperty && isOpen && <PropertyDetailsForm property={selectedProperty} onCloseForm={handleCloseForm} />}
    </div>
  );
};

export default HomePage;
