import React, { useState } from 'react';
import './MortgageCalculator.css';


function MortgageCalculator() {
  const [mortgageType, setMortgageType] = useState('');

  const handleMortgageTypeChange = (event) => {
    setMortgageType(event.target.value);
  };

  const calculateMortgage = (mortgageType) => {
    // Ваша логика для расчета ипотеки
    // Здесь можно использовать значение mortgageType для определения выбранного типа ипотеки
  };

  return (
    <div>
      <h2>Калькулятор ипотеки</h2>
      <div>
        <label htmlFor="mortgageType">Выберите тип ипотеки:</label>
        <select id="mortgageType" value={mortgageType} onChange={handleMortgageTypeChange}>
          <option value="">Выберите тип ипотеки</option>
          <option value="dalnevostochnaya">Дальневосточная ипотека</option>
          <option value="family">Семейная ипотека</option>
          <option value="government">Господдержка</option>
        </select>
      </div>
      {mortgageType && (
        <div>
          <h3>Расчет ипотеки: {mortgageType}</h3>
          {/* Вставьте здесь ваш калькулятор ипотеки */}
          <button onClick={() => calculateMortgage(mortgageType)}>Рассчитать</button>
        </div>
      )}
    </div>
  );
}

export default MortgageCalculator;
