import React, { useState, useEffect } from 'react';
import PropertyCard from './PropertyCard';
import PropertyDetailsForm from './PropertyDetailsForm';
import './HomePage.css';

const HomePage = () => {
  const [selectedProperty, setSelectedProperty] = useState(null);
  const [isOpen, setIsOpen] = useState(false);
  const [filteredProperties, setFilteredProperties] = useState([]);

  const [imageIndexes, setImageIndexes] = useState({
    beautifulHouse: 0,
    cozyApartment: 0,
  });

  const beautifulHouseImages = ['/1.jpg', '/2.jpg', '/3.jpg', '/4.jpg'];
  const cozyApartmentImages = ['/apartment-1.jpg', '/apartment-2.jpg', '/apartment-3.jpg', '/apartment-4.jpg'];

  useEffect(() => {
    const interval = setInterval(() => {
      setImageIndexes(prevIndexes => ({
        ...prevIndexes,
        beautifulHouse: (prevIndexes.beautifulHouse + 1) % beautifulHouseImages.length,
        cozyApartment: (prevIndexes.cozyApartment + 1) % cozyApartmentImages.length,
      }));
    }, 3000);
  
    return () => clearInterval(interval);
  }, [beautifulHouseImages, cozyApartmentImages]);
  
  const handleTitleClick = (property) => {
    setSelectedProperty(property);
    setIsOpen(true);
  };

  const handleCloseForm = () => {
    setIsOpen(false);
    setSelectedProperty(null);
  };

  return (
    <div className="homepage-container">
      <h1 className="homepage-heading">Добро пожаловать на главную страницу нашего приложения по недвижимости</h1>
      <div className="property-list">
        <h2>Популярные предложения</h2>
        <div className="property-cards">
          <div>
            <PropertyCard
              key="beautifulHouse"
              property={{
                id: 1,
                title: 'Барн хаус',
                description: 'Каркасный дом',
                price: '9 000 000 рублей',
                image1: '/1.jpg',
                image2: '/2.jpg',
                image3: '/3.jpg',
                image4: '/4.jpg',
                description: 'Описание недвижимости',
                location: 'Приморский край',
                squaremeter: '50 кв.м',
                description2:'Застройщик акредитован, ипотека Сбербанк, Дом РФ, Маткапитал подходит.'
              }}
              onTitleClick={handleTitleClick}
              onCloseForm={handleCloseForm}
            />
          </div>
          <div>
            <PropertyCard
              key="cozyApartment"
              property={{
                id: 2,
                title: 'Сакура',
                description: 'Блочный дом',
                price: '7 259 663 рублей',
                image1: '/apartment-1.jpg',
                image2: '/apartment-2.jpg',
                image3: '/apartment-3.jpg',
                image4: '/apartment-4.jpg',
                description: 'Описание недвижимости',
                location: 'Приморский край',
                squaremeter: '120 кв.м',
                description2:'Застройщик акредитован, ипотека Сбербанк, Дом РФ, Маткапитал подходит, строительство дома без первоначального взноса, срок строительства: коробка дома - 3 месяца, White box - 6 месяцев, подготовка участка и фасад - считаются индивидуально'
              }}
              onTitleClick={handleTitleClick}
              onCloseForm={handleCloseForm}
            />
          </div>
        </div>
      </div>
      {selectedProperty && isOpen && <PropertyDetailsForm property={selectedProperty} onCloseForm={handleCloseForm} />}
    </div>
  );
};

export default HomePage;
